import { useParams } from "react-router-dom"; // pozwala na użycie Hooka useParam
import { useEffect, useState } from "react";

const Update = () => {
  const { id } = useParams(); // na podstawie routingu z app.js przyjmujemy paratmetr id który możemy użyć w obecnym komponencie jako obiekt
  return (
    <div className="page update">
      <h2>Update - {id} </h2> {/* dynamiczne odwolanie do id*/}
    </div>
  );
};

export default Update;
