import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

import Login from "../src/pages/Login";
import SignUp from "../src/pages/SignUp";
import Homepage from "../src/pages/Homepage";
// pages
import Home from "./pages/Home";
import Create from "./pages/Create";
import Update from "./pages/Update";
import Offer from "./pages/Offer";
import React, { useState, useEffect } from "react";

function App() {
  const [token, setToken] = useState(false); // TOKEN PRZECHOWUJACY LOGOWANIE UŻYTKOWNIKA

  const debugtoken = () => {
    console.log(token);
  };

  if (token) {
    sessionStorage.setItem("token", JSON.stringify(token));
  }

  useEffect(() => {
    if (sessionStorage.getItem("token")) {
      let data = JSON.parse(sessionStorage.getItem("token"));
      setToken(data);
    }
  }, []);

  return (
    <BrowserRouter>
      <nav>
        <h1>Supa Smoothies</h1>
        <Link to="/">Home</Link>
        <Link to="/create">Create New Smoothie</Link>
        {token && <Link to="/homepage">Profil</Link>}
        {!token && <Link to="/login">Login</Link>}
      </nav>
      <div>
        Debug
        <button onClick={debugtoken}>DEBUG TOKEN</button>
      </div>
      <Routes>
        <Route path="/" element={<Home />} />
        {token ? (
          <Route path={"/homepage"} element={<Homepage token={token} />} />
        ) : (
          ""
        )}
        <Route path="/create" element={<Create />} />
        <Route path="/:id" element={<Update />} />
        <Route path="/:id" element={<Offer />} />
        <Route path="/login" element={<Login setToken={setToken} />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/homepage" element={<Homepage token={setToken} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
