import { Link } from "react-router-dom";
import supabase from "../config/supabaseClient";
import { useNavigate } from "react-router-dom";

const SmoothieCard = ({ smoothie, onDeleteProp }) => {
  // destrukturyzacja, Zawartość obietu otrzymujemy z propa z Home.js
  let navigate = useNavigate();

  //Inicjalizacja daty
  const time = new Date();
  // FUNKCJA OBLICZAJĄCA CZAS OD DODANIA { KALKULACJA W MINUTACH}
  function timeCalc() {
    let createdTime =
      parseInt(smoothie.created_at.slice(0, 4)) * 8760 +
      parseInt(smoothie.created_at.slice(5, 7)) * 720 +
      parseInt(smoothie.created_at.slice(8, 10)) * 24;
    let currentTime =
      time.getFullYear() * 8760 +
      (time.getMonth() + 1) * 720 +
      time.getDate() * 24;
    //funkcja zwraca skonfigurowanego strniga
    let timeDif = currentTime - createdTime;
    let outputValue;

    if (timeDif <= 48) {
      outputValue = "NOWE";
    } else if (timeDif > 48) {
      outputValue = "DODANO " + timeDif / 24 + " DNI TEMU";
    } else {
      outputValue = "error";
    }
    return outputValue;
  }

  // DO SCRAPOWANIA
  function openOffer(e) {
    e.preventDefault();
    e.stopPropagation();
    console.log("klik na" + e.target);
  }

  // FUNKCJA USUWAJĄCA WPIS
  const handleDelete = async () => {
    const { data, error } = await supabase
      .from("smoothies")
      .delete()
      .eq("id", smoothie.id) // przekazanne z SmoothieCard po destrukturyzacji
      .select();

    if (error) {
      console.log(error);
    }
    if (data) {
      console.log("usunieto wpis");
      console.log(data);
      onDeleteProp(smoothie.id);
    }
  };

  return (
    <div className="smoothie-card" onClick={openOffer}>
      <h3>{smoothie.title}</h3>

      <div className="rating">{smoothie.rating}</div>
      <div className="salary">
        {smoothie.salary_low} - {smoothie.salary_top}zł
      </div>
      <div className="data">
        <div>
          <p>{timeCalc()}</p>
        </div>
      </div>
      <div className="position-level">{smoothie.position_level}</div>
      <div className="company-name">{smoothie.company_name}</div>
      <div className="button">
        <Link to={"/" + smoothie.id + "/edit"}>
          <i className="material-icons">edit</i> {/*MATERIAL ICON */}
        </Link>
        <i className="material-icons" onClick={handleDelete}>
          delete
        </i>
        {/*MATERIAL ICON */}
        <Link to={"/" + smoothie.id}>
          <i className="material-icons"> OPEN OFFER </i>
        </Link>
      </div>
    </div>
  ); //return zwraca template
};

export default SmoothieCard; // export componentu na zewnątrz

