import React from "react";
import { useNavigate } from "react-router-dom";
import supabase from "../config/supabaseClient";
import { Link } from "react-router-dom";

const Homepage = ({ token }) => {
  let navigate = useNavigate();

  //funkcja sprawdzajaca id
  async function checkID() {
    const {
      data: { user },
    } = await supabase.auth.getUser();
    console.log(user.id);
  }

  //funkcja logout
  function handleLogout() {
    sessionStorage.removeItem("token");
    supabase.auth.signOut();
    token = false;
    navigate("/", { token });
    window.location.reload(true);
  }

  return (
    <div className="page">
      <h1 className="login__title">Profil</h1>
      <div className="profile_card">
        <img
          src="https://lzjwjilirbqiypntnadj.supabase.co/storage/v1/object/public/logos/user.webp"
          alt=""
        />
        <h3>Welcome back, {token.user.user_metadata.full_name}</h3>
        <button onClick={checkID}>Check uuid</button>
        <button onClick={handleLogout}>Logout</button>
        <Link to="/CreatedOffers"> SHOW YOUR OFFERS</Link>
      </div>
    </div>
  );
};

export default Homepage;
