import { Link } from "react-router-dom";
import supabase from "../config/supabaseClient";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

const SmoothieCard = ({ smoothie, onDeleteProp, token }) => {
  const [favFlag, setFavFlag] = useState(false); // PAMIETAĆ O RÓZNICY MIEDZY WYSWETILANYM STANEM A FALGA W SKRYPCIE
  // USER WIDZI PUSTA GWIAZDKE -> PO KLIKNIECIU DODA DO ULUBIONY
  // UI WYSTWIETLA OBECNY STAN
  // destrukturyzacja, Zawartość obietu otrzymujemy z propa z Home.js
  let navigate = useNavigate();

  //Inicjalizacja daty
  const time = new Date();
  // FUNKCJA OBLICZAJĄCA CZAS OD DODANIA { KALKULACJA W MINUTACH}
  function timeCalc() {
    let createdTime =
      parseInt(smoothie.created_at.slice(0, 4)) * 8760 +
      parseInt(smoothie.created_at.slice(5, 7)) * 720 +
      parseInt(smoothie.created_at.slice(8, 10)) * 24;
    let currentTime =
      time.getFullYear() * 8760 +
      (time.getMonth() + 1) * 720 +
      time.getDate() * 24;
    //funkcja zwraca skonfigurowanego strniga
    let timeDif = currentTime - createdTime;
    let outputValue;

    if (timeDif <= 48) {
      outputValue = "NOWE";
    } else if (timeDif > 48) {
      outputValue = "DODANO " + timeDif / 24 + " DNI TEMU";
    } else {
      outputValue = "error";
    }
    return outputValue;
  }

  // DO SCRAPOWANIA
  function openOffer(e) {
    e.preventDefault();
    e.stopPropagation();
    console.log("klik na" + e.target);
  }
  // FUNKCJA LIKE
  const handleFav = async () => {
    console.log(smoothie.liked_by);

    //console.log(token.user.id); // działą wyswietla user id

    if (favFlag === true) {
      const { data, error } = await supabase
        .from("smoothies")
        .update({ liked_by: [token.user.id] })
        .eq("id", smoothie.id)
        .select("liked_by");

      if (error) {
        console.log("error");
      }
      if (data) {
        console.log("updated fav");
      }
      setFavFlag(!favFlag);
    } else if (favFlag === false) {
      console.log("usunieto");
      setFavFlag(!favFlag);
    }
  };

  // FUNKCJA USUWAJĄCA WPIS
  const handleDelete = async () => {
    const { data, error } = await supabase
      .from("smoothies")
      .delete()
      .eq("id", smoothie.id) // przekazanne z SmoothieCard po destrukturyzacji
      .select();

    if (error) {
      console.log(error);
    }
    if (data) {
      console.log("usunieto wpis");
      console.log(data);
      onDeleteProp(smoothie.id);
    }
  };

  return (
    <div className="smoothie-card" onClick={openOffer}>
      <h3>{smoothie.title}</h3>

      <div className="rating">{smoothie.rating}</div>
      <div className="salary">
        {smoothie.salary_low} - {smoothie.salary_top}zł
      </div>
      <div className="data">
        <div>
          <p>{timeCalc()}</p>
        </div>
      </div>
      <div className="position-level">{smoothie.position_level}</div>
      <div className="company-name">{smoothie.company_name}</div>
      <div className="button">
        <Link to={"/" + smoothie.id + "/edit"}>
          <i className="material-icons">edit</i> {/*MATERIAL ICON */}
        </Link>
        <i className="material-icons" onClick={handleDelete}>
          delete
        </i>
        {/*POLUBIENIE KARTY*/}

        <button className="material-icons" onClick={handleFav}>
          {!favFlag && <i className="material-icons">star</i>}
          {favFlag && <i className="material-icons">star_border</i>}
        </button>
        <button
          onClick={() => {
            console.log(smoothie);
          }}
        >
          DEBUG
        </button>

        <Link to={"/" + smoothie.id}>
          <i className="material-icons"> OPEN OFFER </i>
        </Link>
      </div>
    </div>
  ); //return zwraca template
};

export default SmoothieCard; // export componentu na zewnątrz

