import supabase from "../config/supabaseClient";
import { useEffect, useState } from "react";

// components

import SmoothieCard from "../components/smoothieCard";

const Home = ({ token }) => {
  const [fetchError, setFetchError] = useState(null); // miejsce przechowywania danych  Tzw. State Reacta
  const [smoothies, setSmoothies] = useState(null); // miejsce przechowywania danych  Tzw. State Reacta
  const [orderBy, setOrderBy] = useState("created_at"); // created_at jest nazwa kolumny w bazie
  const [orderLocaly, setOrderLocaly] = useState("created_at");
  const [orderDirectionAscd, setOrderDirectionAscd] = useState(true);
  const [favFlag, setFavFlag] = useState(false);

  // SEARCH FUNCTION

  const [searchInput, setSearchInput] = useState("");

  const handleChange = (e) => {
    e.preventDefault();
    setSearchInput(e.target.value);
  };

  if (searchInput.length > 0) {
    smoothies.filter((smoothie) => {
      return smoothie.title.match(searchInput);
    });
  }
  ///

  //Funkcja usuwająca wpis lokalnie
  const handleDelete = (id) => {
    setSmoothies((pervSmoothies) => {
      return pervSmoothies.filter((sm) => sm.id !== id);
    });
  };

  const orderDirectionFoo = (bool) => {
    if (bool) {
      return <p>Truee</p>;
    }
    return <p>Falsee</p>;
  };

  //funkcja fetchujaca dane z database
  useEffect(() => {
    const fetchSmoothies = async () => {
      const { data, error } = await supabase
        .from("smoothies")
        .select()
        .order(orderBy, { ascending: false }); // const { data, error } -> destrukturyzacja obiektu po lewej

      if (error) {
        setFetchError("Can;t fetch ");
        setSmoothies(null);
        console.log(error);
      }
      if (data) {
        setSmoothies(data);
        setFetchError(null);
      }
    };

    fetchSmoothies();
  }, []); //[orderBy] powoduje wywołanie Hooka useEffect za każdym użyciem orderby // brak tylko pojedyncze wywołanie

  function filteredSmoothies() {
    const filtered = smoothies.filter((smoothies) => {
      return smoothies.title === "Beer Drink ";
    });
    console.log(" filtr po Beer Drink ");
    console.log(filtered);
  }

  return (
    <div className="page home">
      {fetchError && <p>{fetchError}</p>}
      {smoothies && (
        <div className="smoothies">
          <div>
            {token.user && (
              <button
                onClick={() => {
                  setFavFlag(!favFlag);
                }}
              >
                SHOW ONLY FAV
              </button>
            )}
          </div>
          {/*debug */}
          <div className="debug">
            <button
              onClick={() => {
                console.log("found users: ");
                console.log(smoothies);
                filteredSmoothies();
              }}
            >
              DEBUG
            </button>
          </div>
          <div>
            <input
              type="text"
              placeholder="Search here"
              onChange={handleChange}
              value={searchInput}
            />

            <table>
              <tr>
                <th>Country</th>
                <th>Continent</th>
              </tr>

              {smoothies.map((country) => {
                <tr>
                  <td>{smoothies.title}</td>
                  <td>{smoothies.rating}</td>
                </tr>;
              })}
            </table>
          </div>

          {/*FILTER WITH FETCH /// SCRAP THAT 
          <div className="order-by">
            <p>Order by:</p>
            <button onClick={() => setOrderBy("created_at")}>
              Time Created
            </button>
            <button onClick={() => setOrderBy("title")}>Title</button>
            <button onClick={() => setOrderBy("rating")}>rating</button>
            {orderBy}
          </div>
          */}

          {/*BETTER SORTING */}
          <div className="order-by">
            <p>Order localy:</p>
            <button onClick={() => setOrderLocaly("created_at")}>
              Time Created NIE DZIAŁĄ
            </button>
            <button
              onClick={() => {
                smoothies.sort(function (a, b) {
                  const nameA = a.title.toUpperCase(); // ignore upper and lowercase
                  const nameB = b.title.toUpperCase(); // ignore upper and lowercase
                  if (nameA > nameB) {
                    return -1;
                  }
                  if (nameA < nameB) {
                    return 1;
                  }
                  // names must be equal
                  return 0;
                });
                if (orderDirectionAscd) {
                  setSmoothies(smoothies.reverse());
                  setOrderLocaly("title asc");
                } else if (!orderDirectionAscd) {
                  setOrderLocaly("title dsc");
                  setSmoothies(smoothies);
                }
              }}
            >
              Title
            </button>
            <button onClick={() => setOrderLocaly("rating")}>
              rating nie działa{" "}
            </button>
            <button
              onClick={() => {
                smoothies.sort(function (a, b) {
                  if (a.rating < b.rating) {
                    return 1;
                  }
                  if (a.rating > b.rating) {
                    return -1;
                  }
                  return 0;
                });
                if (orderDirectionAscd) {
                  setSmoothies(smoothies.reverse());
                  setOrderLocaly("title asc");
                } else if (!orderDirectionAscd) {
                  setOrderLocaly("title dsc");
                  setSmoothies(smoothies);
                }
              }}
            >
              Rating
            </button>
            {/*KIERUNEK SORTOWANIA*/}
            {orderLocaly} {/*wyświetla rodzaj sortowania  */}
            {orderDirectionAscd && (
              <div>
                Ascnding true
                <button
                  onClick={() => setOrderDirectionAscd(!orderDirectionAscd)}
                >
                  {<i className="material-icons">arrow_drop_up</i>}
                </button>
              </div>
            )}
            {!orderDirectionAscd && (
              <div>
                Ascnding false
                <button
                  onClick={() => setOrderDirectionAscd(!orderDirectionAscd)}
                >
                  {<i className="material-icons">arrow_drop_down</i>}
                </button>
              </div>
            )}
          </div>
          {/*WYŚWIETLANIE*/}
          <div className="smoothie-grid">
            {smoothies.map((smoothie) => {
              if (favFlag) {
                if (smoothie.liked_by.includes(token.user.id)) {
                  return (
                    <SmoothieCard
                      key={smoothie.id}
                      smoothie={smoothie}
                      onDeleteProp={handleDelete}
                      token={token}
                    />
                  );
                }
              } else {
                return (
                  <SmoothieCard
                    key={smoothie.id}
                    smoothie={smoothie}
                    onDeleteProp={handleDelete}
                    token={token}
                  />
                );
              }
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
